import c from './Users.module.css';

const Users = (props) => {

    let onFollowClick = (id) => {
        props.follow(id);
    }
    
    let onUnfollowClick = (id) => {
        props.unfollow(id);
    }

    return (
        <div>
            {
                props.allUsers.map(au => <div className={c.Users}>
                    <div className={c.User}>
                        <div>
                            <img src="https://image.flaticon.com/icons/png/512/108/108186.png" className={c.Avatar} />
                        </div>
                        <div className={c.Info}>
                            <h3 className={c.Username}>{au.name}</h3>
                            <p>Клуб: {au.club}</p>
                            <p>Количество постов: {au.postsCounter}</p>
                            <p>Рейтинг: {au.raiting}</p>
                        </div>
                    </div>
                    {au.followed ? <button onClick={onUnfollowClick(au.id)}>Отписаться</button> : <button onClick={onFollowClick(au.id)}>Подписаться</button>}<hr />    
                </div>)
            }
        </div>
    )
}

export default Users;
