import c from './Dialogs.module.css';
import React from 'react';
import { NavLink } from 'react-router-dom';

const DialogItems = (props) => {
    let path = "/dialogs/" + props.id;
    return (
        <div className={c.User}>
            <img src="https://image.flaticon.com/icons/png/512/108/108186.png" className={c.Avatar} />
            <span><NavLink to={path} activeClassName={c.Active}>{props.name}</NavLink></span><hr />
        </div>
    )
}

const Message = (props) => {
    return(
        <div className={c.Dialog}>{props.message}</div> 
    )
}

const Dialogs = (props) => {

    let dialogsData = [
        {id:1, name: "Pavel"},
        {id:2, name: "Petr"},
        {id:3, name: "Dima"},
        {id:4, name: "Anton"},
    ]

    let messagesData = [
        {id:1, message: "Hello"},
        {id:2, message: "Hi"},
        {id:3, message: "How are you?"},
        {id:4, message: "Bye"},
    ]

    let dialogsElements = dialogsData.map(d => <DialogItems name={d.name} id={d.id}/>);

    let messagesElements = messagesData.map(m => <Message message={m.message}/>)

    return (
        <div className={c.Dialogs}>
            <div className={c.Users}>
                {dialogsElements}
            </div>
            <div className={c.Line}></div>
            <div className={c.Messages}>
                <Message message={messagesData[0].message}/>
                <Message message={messagesData[1].message}/>
                <Message message={messagesData[2].message}/>
                <Message message={messagesData[3].message}/>
            </div>
        </div>
    )
}

export default Dialogs;
