import React from 'react';
import c from './AddPost.module.css';

const AddPost = (props) => {

    let newPostText = React.createRef();
    let newPostTitle = React.createRef();

    let addNewPost = () => {
        let title = newPostTitle.current.value;
        let text = newPostText.current.value;
        props.addPost(title, text)
        newPostTitle.current.value = "";
        newPostText.current.value = "";
    }

    let onTitleChange = () => {
        let title = newPostTitle.current.value;
        props.changeNewPostTitle(title);

    }

    let onTextChange = () => {
        let text = newPostText.current.value;
        props.changeNewPostText(text);
    }

    return(
        <div className={c.AddPost}>
            <div className={c.Title}>
                <p>Заголовок:</p>
                <input onChange={onTitleChange} ref={newPostTitle} value={props.newPostTitle}></input>
            </div>
            <div className={c.Text}>
                <p>Текст поста:</p>
                <textarea onChange={onTextChange} ref={newPostText} value={props.newPostText}></textarea>
            </div>
            <button onClick={addNewPost}>Добавить пост</button>
        </div>
    )
}

export default AddPost;
